import { Outlet, Link, useLocation } from "react-router-dom";
import { useState, useEffect } from "react";

import jaguaLogoGray from '../../assets/visual_identity/logo/jagua_logo_gray.png';

import "./Navbar.css";

const Navbar = () => {
  const location = useLocation(); // Obtenha a localização atual usando o hook useLocation do React Router
  const [highlightStyle, setHighlightStyle] = useState({
    // Estado para armazenar o estilo do destaque
    left: 0,
    width: 0,
    opacity: 0,
    transition: "1.2s",
  });

  const [initialHighlightWidth, setInitialHighlightWidth] = useState(0);

  useEffect(() => {
    const navLinks = document.querySelectorAll(".navLink");

    navLinks.forEach((link) => {
      if (link.pathname === location.pathname) {
        link.classList.add("active");
        updateHighlight(link, link.innerText);
      } else {
        link.classList.remove("active");
      }
    });

    const initialLink = document.querySelector(".navLink");
    if (initialLink) {
      const initialWidth = initialLink.offsetWidth;
      setInitialHighlightWidth(initialWidth);
    }
  }, [location]);


  const updateHighlight = (link, linkText) => {
    const { offsetLeft } = link;

    const span = document.createElement('span');
    span.innerText = linkText;
    document.body.appendChild(span);
    const width = span.offsetWidth;
    document.body.removeChild(span);

    const newHighlightStyle = {
      ...highlightStyle,
      left: offsetLeft + "px",
      width: width + "px",
      opacity: 0.7,
    };
    setHighlightStyle(newHighlightStyle);
  };


  const handleMouseOver = (e) => {
    updateHighlight(e.target);
  };

  const handleMouseOut = () => {
    const activeLink = document.querySelector(".navLink.active");
    if (activeLink) {
      updateHighlight(activeLink, activeLink.innerText);
    } else {
      setHighlightStyle((prevStyle) => ({
        ...prevStyle,
        opacity: 0,
      }));
    }
  };


  const handleResize = () => {
    const activeLink = document.querySelector(".navLink.active");
    if (activeLink) {
      updateHighlight(activeLink);
    } else {
      setHighlightStyle((prevStyle) => ({
        ...prevStyle,
        opacity: 0,
      }));
    }
  };

  useEffect(() => {
    window.addEventListener("resize", handleResize);
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  return (
    <header className="myHeader">
      <nav>
        <input type="checkbox" className="check" />
        <label htmlFor="check" className="toggleResponsiveMenu">
          <i className="fas fa-bars"></i>
        </label>

        <div className="logoJagua">
          <Link to="/" className="navLink">
          <img
            src={jaguaLogoGray}
            alt="Logo do site"
          />
          </Link>
        </div>
        <ul>
          <li>
            <Link
              to="/"
              className="navLink"
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
            >
              Home
            </Link>
          </li>
          <li>
            <Link
              to="/aboutMe"
              className="navLink"
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
            >
              Sobre mim
            </Link>
          </li>
          <li>
            <Link
              to="/stickers"
              className="navLink"
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
            >
              Adesivos
            </Link>
          </li>
          <li>
            <Link
              to="/prints"
              className="navLink"
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
            >
              Prints
            </Link>
          </li>
          <li>
            <Link
              to="/shirts"
              className="navLink"
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
            >
              Camisetas
            </Link>
          </li>

          <div id="highlightNavBar" style={highlightStyle}></div>
        </ul>

        <Link to="/login" className="loginButton">
          Entrar
        </Link>
        <div className="smartphoneLogin">
          <Link to="/login">
            <i className="fas fa-sign-in-alt"></i>
          </Link>
        </div>
      </nav>

      <Outlet />
    </header>
  );
};

export default Navbar;
