import { Outlet, Link, useLocation } from "react-router-dom";
import { useEffect, useMemo } from "react";

import jaguaLogoGray from '../../assets/visual_identity/logo/jagua_logo_gray.png';

import "./Navbar.css";

const Navbar = () => {
  // -- Logica do destaque laranja

  // Pega objeto que representa pag atual
  const location = useLocation();

  // Selecione todos os links de navegacao
  const navLinks = useMemo(() => {
    return document.querySelectorAll(".navLink");
  });

  // Percorra cada link de navegacao
  useEffect(() => {
    navLinks.forEach((link) => {
      // Verifique se a URL do link corresponde a URL atual
      console.log(link);
    });
  }, [location, navLinks]);


  // -- Renderizacao da NavBar --
  return (
    <header className="myHeader">
      <nav>
        <input type="checkbox" className="check" />
        <label htmlFor="check" className="toggleResponsiveMenu">
          <i className="fas fa-bars"></i>
        </label>

        <div className="logoJagua">
          <Link to="/" className="navLink">
            <img
              src={jaguaLogoGray}
              alt="Logo do site"
            />
          </Link>
        </div>

        <ul>
          <li>
            <Link to="/" className="navLink">Home</Link>
          </li>
          <li>
            <Link to="/aboutMe" className="navLink">Sobre mim</Link>
          </li>
          <li>
            <Link to="/stickers" className="navLink">Adesivos</Link>
          </li>
          <li>
            <Link to="/prints" className="navLink">Prints</Link>
          </li>
          <li>
            <Link to="/shirts" className="navLink">Camisetas</Link>
          </li>

          <div id="highlightNavBar"></div>
        </ul>

        <Link to="/login" className="loginButton">Entrar</Link>
        <div className="smartphoneLogin">
          <Link to="/login">
            <i className="fas fa-sign-in-alt"></i>
          </Link>
        </div>
      </nav>

      <Outlet />
    </header>
  );
}

export default Navbar;
