import { Outlet, Link, useLocation } from "react-router-dom";
import { useState, useEffect } from "react";

import jaguaLogoGray from '../../assets/visual_identity/logo/jagua_logo_gray.png';

import "./Navbar.css";

const Navbar = () => {
  const location = useLocation();
  const [highlightStyle, setHighlightStyle] = useState({
    left: 0,
    width: 0,
    opacity: 0,
    transition: "1.2s",
  });

  const [indexHighlightStyle, setIndexHighlightStyle] = useState({
    left: 0,
    width: 0,
    opacity: 0,
    transition: "1.2s",
  });

  useEffect(() => {
    const navLinks = document.querySelectorAll(".navLink");

    navLinks.forEach((link) => {
      if (link.pathname === location.pathname) {
        link.classList.add("active");
        if (link.pathname === "/") {
          updateIndexHighlight(link);
        } else {
          updateHighlight(link);
        }
      } else {
        link.classList.remove("active");
      }
    });
  }, [location]);

  const updateHighlight = (link) => {
    const { offsetLeft, offsetWidth } = link;
    const newHighlightStyle = {
      ...highlightStyle,
      left: offsetLeft + "px",
      width: offsetWidth + "px",
      opacity: 0.7,
    };
    setHighlightStyle(newHighlightStyle);
  };

  const updateIndexHighlight = (link) => {
    const { offsetLeft, offsetWidth } = link;
    const newIndexHighlightStyle = {
      ...indexHighlightStyle,
      left: offsetLeft + "px",
      width: offsetWidth + "px",
      opacity: 0.7,
    };
    setIndexHighlightStyle(newIndexHighlightStyle);
  };

  const handleMouseOver = (e) => {
    if (location.pathname === "/") {
      updateIndexHighlight(e.target);
    } else {
      updateHighlight(e.target);
    }
  };

  const handleMouseOut = () => {
    const activeLink = document.querySelector(".navLink.active");
    if (activeLink) {
      if (activeLink.pathname === "/") {
        updateIndexHighlight(activeLink);
      } else {
        updateHighlight(activeLink);
      }
    } else {
      setHighlightStyle((prevStyle) => ({
        ...prevStyle,
        opacity: 0,
      }));
      setIndexHighlightStyle((prevStyle) => ({
        ...prevStyle,
        opacity: 0,
      }));
    }
  };

  const handleResize = () => {
    const activeLink = document.querySelector(".navLink.active");
    if (activeLink) {
      if (activeLink.pathname === "/") {
        updateIndexHighlight(activeLink);
      } else {
        updateHighlight(activeLink);
      }
    } else {
      setHighlightStyle((prevStyle) => ({
        ...prevStyle,
        opacity: 0,
      }));
      setIndexHighlightStyle((prevStyle) => ({
        ...prevStyle,
        opacity: 0,
      }));
    }
  };

  useEffect(() => {
    window.addEventListener("resize", handleResize);
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  return (
    <header className="myHeader">
      <nav>
        <input type="checkbox" className="check" />
        <label htmlFor="check" className="toggleResponsiveMenu">
          <i className="fas fa-bars"></i>
        </label>

        <div className="logoJagua">
          <Link to="/" className="navLink">
            <img src={jaguaLogoGray} alt="Logo do site" />
          </Link>
        </div>
        <ul>
          <li>
            <Link
              to="/"
              className="navLink"
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
              onClick={(e) => {
                updateIndexHighlight(e.target);
              }}
            >
              Home
            </Link>
          </li>
          <li>
            <Link
              to="/aboutMe"
              className="navLink"
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
            >
              Sobre mim
            </Link>
          </li>
          <li>
            <Link
              to="/stickers"
              className="navLink"
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
            >
              Adesivos
            </Link>
          </li>
          <li>
            <Link
              to="/prints"
              className="navLink"
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
            >
              Prints
            </Link>
          </li>
          <li>
            <Link
              to="/shirts"
              className="navLink"
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
            >
              Camisetas
            </Link>
          </li>

          <div
            id="highlightNavBar"
            style={location.pathname === "/" ? indexHighlightStyle : highlightStyle}
          ></div>
        </ul>

        <Link to="/login" className="loginButton">
          Entrar
        </Link>
        <div className="smartphoneLogin">
          <Link to="/login">
            <i className="fas fa-sign-in-alt"></i>
          </Link>
        </div>
      </nav>

      <Outlet />
    </header>
  );
};

export default Navbar;
