import { Outlet, Link, useLocation } from "react-router-dom";
import { useState, useEffect } from "react";

import LogoLink from "./LogoLink.js";

import "./Navbar.css";

const Navbar = () => {
  // Definicao dos hooks
  const location = useLocation(); // usando hook para obter localizacao atual
  const [highlightStyle, setHighlightStyle] = useState({
    // Estado para armazenar estilo do destaque
    left: 0,
    width: 0,
    opacity: 0,
    transition: "1.2s"
  });

  useEffect(() => {
    // Selecione todos os links de navegação
    const navLinks = document.querySelectorAll(".navLink");

    // Percorra cada link de navegação
    navLinks.forEach((link) => {
      // Verifique se o URL do link corresponde ao URL atual
      if (link.pathname === location.pathname) {
        // Adicione a classe 'active' ao link correspondente
        link.classList.add('active'); 

        // Atualize o destaque quando a localizacao muda
        updateHighlight(link); 
      }
      else {
        link.classList.remove("active");
      }  
    });

  }, [location]);


  const updateHighlight = (link) => {
    const { offsetLeft, offsetWidth } = link;
    const newHighlightStyle = {
      ...highlightStyle,
      left: offsetLeft + "px",
      width: offsetWidth + "px",
      opacity: 0.7
    };

    setHighlightStyle(newHighlightStyle);
  };

  const handleMouseOver = (event) => {
    updateHighlight(event.target);
  };

  const handleMouseOut = () => {
    const activeLink = document.querySelector(".navLink.active");

    if (activeLink) {
      updateHighlight(activeLink);
    }

    else {
      setHighlightStyle((prevStyle) => ({
        ...prevStyle,
        opacity: 0
      }));
    }
  };




  // NavBar com destaque laranja quando passa o mouse
  let highlight = document.querySelector("#highlightNavBar");
  let item = document.querySelectorAll("a.navLink");

  function indicator(e, fastTransition) {
    // Para fazer transicao mais rapida para mouseover e mais lenta para mouseout
    highlight.style.transition = fastTransition ? "0.3s" : "1.2s";

    highlight.style.left = e.offsetLeft + "px";
    highlight.style.width = e.offsetWidth + "px";
    highlight.style.opacity = 0.7;
  }

  // Obtenha a URL atual da página
  let currentUrl = window.location.href;

  // Percorra cada link de navegação
  navLinks.forEach(function(link) {
    // Verifique se o URL do link corresponde ao URL atual
    if (link.href === currentUrl) {
        // Adicione a classe 'active' ao link correspondente
        link.classList.add('active'); 
    }
  });

  item.forEach(link => {
      // Carregou pagina
      if (link.classList.contains("active")) {
          indicator(link, false); // Destaque primariamente esse link (que eh o atual)
      }

      // Quando usuario passar mouse na barra de menu
      link.addEventListener("mouseover", (e)=> {
          indicator(e.target, true); // Destaque este link (no qual usuario passa o mouse), de forma rapida
      });

      // Quando usuario tirar mouse da barra de menu
      link.addEventListener("mouseout", () => {
          let activeLink = document.querySelector(".navLink.active"); // Volte o destaque, devagar, a este item
          if (activeLink) {
              indicator(activeLink, false);
          } 

          else {
              highlight.style.opacity = 0;
          }
      });
  });

  // Para que ao redimensionar nao pareca 'bugado'
  window.addEventListener("resize", function() {
    let activeLink = document.querySelector(".navLink.active"); // Volte o destaque, devagar, a este item
    if (activeLink) {
        indicator(activeLink, false);
    } 

    else {
        highlight.style.opacity = 0;
    }
  });

  // -- Renderizacao da NavBar --
  return (
    <header className="myHeader">
      <nav>
        <input type="checkbox" className="check" />
        <label htmlFor="check" className="toggleResponsiveMenu">
          <i className="fas fa-bars"></i>
        </label>

        <LogoLink />

        <ul>
          <li>
            <Link to="/" className="navLink">Home</Link>
          </li>
          <li>
            <Link to="/aboutMe" className="navLink">Sobre mim</Link>
          </li>
          <li>
            <Link to="/stickers" className="navLink">Adesivos</Link>
          </li>
          <li>
            <Link to="/prints" className="navLink">Prints</Link>
          </li>
          <li>
            <Link to="/shirts" className="navLink">Camisetas</Link>
          </li>

          <div id="highlightNavBar"></div>
        </ul>

        <Link to="/login" className="loginButton">Entrar</Link>
        <div className="smartphoneLogin">
          <Link to="/login">
            <i className="fas fa-sign-in-alt"></i>
          </Link>
        </div>
      </nav>

      <Outlet />
    </header>
  );
}

export default Navbar;
