import { Outlet, Link, useLocation } from "react-router-dom";
import { useState, useEffect } from "react";

import jaguaLogoGray from "../../assets/visual_identity/logo/jagua_logo_gray.png";
import "./Navbar.css";

const Navbar = () => {
  const location = useLocation();
  const [highlightStyle, setHighlightStyle] = useState({
    left: 0,
    width: 0,
    opacity: 0,
    transition: "1.2s",
  });

  useEffect(() => {
    const navLinks = document.querySelectorAll(".navLink");

    const updateHighlight = (link) => {
      const { offsetLeft, offsetWidth } = link;
      const newHighlightStyle = {
        ...highlightStyle,
        left: offsetLeft + "px",
        width: offsetWidth + "px",
        opacity: 0.7,
      };
      setHighlightStyle(newHighlightStyle);
    };

    navLinks.forEach((link) => {
      if (link.pathname === location.pathname) {
        link.classList.add("active");
        updateHighlight(link);
      } else {
        link.classList.remove("active");
      }
    });
  }, [location, highlightStyle]);

  const handleMouseOver = (e) => {
    const link = e.target;
    updateHighlight(link);
  };

  const handleMouseOut = () => {
    const activeLink = document.querySelector(".navLink.active");
    if (activeLink) {
      updateHighlight(activeLink);
    } else {
      setHighlightStyle((prevStyle) => ({
        ...prevStyle,
        opacity: 0,
      }));
    }
  };

  const handleResize = () => {
    const activeLink = document.querySelector(".navLink.active");
    if (activeLink) {
      updateHighlight(activeLink);
    } else {
      setHighlightStyle((prevStyle) => ({
        ...prevStyle,
        opacity: 0,
      }));
    }
  };

  useEffect(() => {
    window.addEventListener("resize", handleResize);
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, [handleResize]);

  return (
    <header className="myHeader">
      {/* restante do c√≥digo */}
    </header>
  );
};

export default Navbar;
