import { Outlet, Link, useLocation } from "react-router-dom";
import { useEffect, useRef, useState } from "react";
import LogoLink from "./LogoLink.js";
import "./Navbar.css";

const Navbar = () => {
  const highlightRef = useRef(null);
  const [currentLink, setCurrentLink] = useState(null);
  const location = useLocation();
  const highlight = highlightRef.current;

  function indicator(link, fastTransition) {
    if (highlight) {
      const rect = link.getBoundingClientRect();
      const scrollLeft = window.pageXOffset || document.documentElement.scrollLeft;
      const offsetLeft = rect.left + scrollLeft;

      highlight.style.transition = fastTransition ? "0.3s" : "1.2s";
      highlight.style.left = offsetLeft + "px";
      highlight.style.width = link.offsetWidth + "px";
      highlight.style.opacity = 0.7;
    }
  }

  useEffect(() => {
    const items = document.querySelectorAll("a.navLink");
    const activeLink = document.querySelector(".navLink.active");

    if (activeLink) {
      setCurrentLink(activeLink);
    } else if (currentLink) {
      setCurrentLink(null);
    }

    function handleMouseOver(e) {
      indicator(e.target, true);
    }

    function handleMouseOut() {
      if (currentLink) {
        indicator(currentLink, false);
      }
    }

    items.forEach((link) => {
      if (link.href === location.pathname) {
        link.classList.add("active");
        indicator(link, false);
      }

      link.addEventListener("mouseover", handleMouseOver);
      link.addEventListener("mouseout", handleMouseOut);
    });

    return () => {
      items.forEach((link) => {
        link.removeEventListener("mouseover", handleMouseOver);
        link.removeEventListener("mouseout", handleMouseOut);
      });
    };
  }, [location.pathname, currentLink]);

  return (
    <header className="myHeader">
      <nav>
        <input type="checkbox" className="check" />
        <label htmlFor="check" className="toggleResponsiveMenu">
          <i className="fas fa-bars"></i>
        </label>

        <LogoLink />

        <ul>
          <li>
            <Link to="/" className="navLink">
              Home
            </Link>
          </li>
          <li>
            <Link to="/aboutMe" className="navLink">
              Sobre mim
            </Link>
          </li>
          <li>
            <Link to="/stickers" className="navLink">
              Adesivos
            </Link>
          </li>
          <li>
            <Link to="/prints" className="navLink">
              Prints
            </Link>
          </li>
          <li>
            <Link to="/shirts" className="navLink">
              Camisetas
            </Link>
          </li>

          <div id="highlightNavBar" ref={highlightRef}></div>
        </ul>

        <Link to="/login" className="loginButton">
          Entrar
        </Link>
        <div className="smartphoneLogin">
          <Link to="/login">
            <i className="fas fa-sign-in-alt"></i>
          </Link>
        </div>
      </nav>

      <Outlet />
    </header>
  );
};

export default Navbar;
