import { Outlet, Link, useLocation } from "react-router-dom";
import { useState, useEffect } from "react";

import LogoLink from "./LogoLink.js";

import "./Navbar.css";

const Navbar = () => {
  const location = useLocation();
  const [highlightStyle, setHighlightStyle] = useState({
    left: 0,
    width: 0,
    opacity: 0,
    transition: "1.2s",
  });
  const [homeHighlightPosition, setHomeHighlightPosition] = useState(null); // Armazena a posição inicial do destaque na página Home

  useEffect(() => {
    const navLinks = document.querySelectorAll(".navLink");

    navLinks.forEach((link) => {
      if (link.pathname === location.pathname) {
        link.classList.add("active");
        updateHighlight(link);
      } else {
        link.classList.remove("active");
      }
    });
  }, [location]);

  useEffect(() => {
    const homeLink = document.querySelector(".navLink[href='/']");

    if (homeLink) {
      const { offsetLeft, offsetWidth } = homeLink;
      const homeHighlightPosition = {
        left: offsetLeft + "px",
        width: offsetWidth + "px",
      };
      setHomeHighlightPosition(homeHighlightPosition);
    }
  }, []);

  const updateHighlight = (link) => {
    const { offsetLeft, offsetWidth } = link;
    const newHighlightStyle = {
      ...highlightStyle,
      left: offsetLeft + "px",
      width: offsetWidth + "px",
      opacity: 0.7,
    };
    setHighlightStyle(newHighlightStyle);
  };

  const handleMouseOver = (e) => {
    updateHighlight(e.target);
  };

  const handleMouseOut = () => {
    const activeLink = document.querySelector(".navLink.active");
    if (activeLink) {
      if (location.pathname === "/") {
        setHighlightStyle((prevStyle) => ({
          ...prevStyle,
          ...homeHighlightPosition, // Restaura a posição inicial na página Home
        }));
      } else {
        updateHighlight(activeLink);
      }
    } else {
      setHighlightStyle((prevStyle) => ({
        ...prevStyle,
        opacity: 0,
      }));
    }
  };

  const handleResize = () => {
    const activeLink = document.querySelector(".navLink.active");
    if (activeLink) {
      updateHighlight(activeLink);
    } else {
      setHighlightStyle((prevStyle) => ({
        ...prevStyle,
        opacity: 0,
      }));
    }
  };

  useEffect(() => {
    window.addEventListener("resize", handleResize);
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  return (
    <header className="myHeader">
      <nav>
        <input type="checkbox" className="check" />
        <label htmlFor="check" className="toggleResponsiveMenu">
          <i className="fas fa-bars"></i>
        </label>

        <LogoLink />

        <ul>
          <li>
            <Link
              to="/"
              className="navLink"
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
            >
              Home
            </Link>
          </li>
          <li>
            <Link
              to="/aboutMe"
              className="navLink"
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
            >
              Sobre mim
            </Link>
          </li>
          <li>
            <Link
              to="/stickers"
              className="navLink"
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
            >
              Adesivos
            </Link>
          </li>
          <li>
            <Link
              to="/prints"
              className="navLink"
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
            >
              Prints
            </Link>
          </li>
          <li>
            <Link
              to="/shirts"
              className="navLink"
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
            >
              Camisetas
            </Link>
          </li>

          <div id="highlightNavBar" style={highlightStyle}></div>
        </ul>

        <Link to="/login" className="loginButton">
          Entrar
        </Link>
        <div className="smartphoneLogin">
          <Link to="/login">
            <i className="fas fa-sign-in-alt"></i>
          </Link>
        </div>
      </nav>

      <Outlet />
    </header>
  );
};

export default Navbar;
