import { Outlet, Link, useLocation } from "react-router-dom";
import { useEffect, useState } from "react";
import LogoLink from "./LogoLink.js";
import "./Navbar.css";

const Navbar = () => {
  const [highlightStyle, setHighlightStyle] = useState({});
  const location = useLocation();

  useEffect(() => {
    const items = document.querySelectorAll("a.navLink");

    function handleMouseOver(e) {
      const link = e.target;
      const linkOffsetLeft = link.offsetLeft;
      const linkOffsetWidth = link.offsetWidth;
      setHighlightStyle({ left: linkOffsetLeft, width: linkOffsetWidth });
    }

    function handleMouseOut() {
      setHighlightStyle({});
    }

    items.forEach((link) => {
      if (link.href === location.pathname) {
        link.classList.add("active");
        const linkOffsetLeft = link.offsetLeft;
        const linkOffsetWidth = link.offsetWidth;
        setHighlightStyle({ left: linkOffsetLeft, width: linkOffsetWidth });
      }

      link.addEventListener("mouseover", handleMouseOver);
      link.addEventListener("mouseout", handleMouseOut);
    });

    return () => {
      items.forEach((link) => {
        link.removeEventListener("mouseover", handleMouseOver);
        link.removeEventListener("mouseout", handleMouseOut);
      });
    };
  }, [location.pathname]);

  return (
    <header className="myHeader">
      <nav>
        <input type="checkbox" className="check" />
        <label htmlFor="check" className="toggleResponsiveMenu">
          <i className="fas fa-bars"></i>
        </label>

        <LogoLink />

        <ul>
          <li>
            <Link to="/" className={`navLink ${location.pathname === "/" ? "active" : ""}`}>
              Home
            </Link>
          </li>
          <li>
            <Link to="/aboutMe" className={`navLink ${location.pathname === "/aboutMe" ? "active" : ""}`}>
              Sobre mim
            </Link>
          </li>
          <li>
            <Link to="/stickers" className={`navLink ${location.pathname === "/stickers" ? "active" : ""}`}>
              Adesivos
            </Link>
          </li>
          <li>
            <Link to="/prints" className={`navLink ${location.pathname === "/prints" ? "active" : ""}`}>
              Prints
            </Link>
          </li>
          <li>
            <Link to="/shirts" className={`navLink ${location.pathname === "/shirts" ? "active" : ""}`}>
              Camisetas
            </Link>
          </li>
        </ul>

        <div id="highlightNavBar" style={highlightStyle}></div>

        <Link to="/login" className="loginButton">
          Entrar
        </Link>
        <div className="smartphoneLogin">
          <Link to="/login">
            <i className="fas fa-sign-in-alt"></i>
          </Link>
        </div>
      </nav>

      <Outlet />
    </header>
  );
};

export default Navbar;
