import { Outlet, Link, useLocation } from "react-router-dom";
import { useState, useEffect, useCallback, useRef } from "react";

import jaguaLogoGray from "../../assets/visual_identity/logo/jagua_logo_gray.png";

import "./Navbar.css";

const Navbar = () => {
  const location = useLocation();
  const [highlightStyle, setHighlightStyle] = useState({
    left: 0,
    width: 0,
    opacity: 0,
    transition: "1.2s",
  });

  const navLinksRef = useRef([]);
  const activeLinkRef = useRef(null);

  const updateHighlight = useCallback((link) => {
    const { offsetLeft, offsetWidth } = link;
    const newHighlightStyle = {
      ...highlightStyle,
      left: offsetLeft + "px",
      width: offsetWidth + "px",
      opacity: 0.7,
    };
    setHighlightStyle(newHighlightStyle);
  }, [highlightStyle]);

  useEffect(() => {
    navLinksRef.current.forEach((link) => {
      if (link.pathname === location.pathname) {
        link.classList.add("active");
        activeLinkRef.current = link;
        updateHighlight(link);
      } else {
        link.classList.remove("active");
      }
    });
  }, [location, updateHighlight]);

  const handleMouseOver = (e) => {
    updateHighlight(e.target);
  };

  const handleMouseOut = useCallback(() => {
    if (activeLinkRef.current) {
      updateHighlight(activeLinkRef.current);
    } else {
      setHighlightStyle((prevStyle) => ({
        ...prevStyle,
        opacity: 0,
      }));
    }
  }, [updateHighlight]);

  const handleResize = useCallback(() => {
    if (activeLinkRef.current) {
      updateHighlight(activeLinkRef.current);
    } else {
      setHighlightStyle((prevStyle) => ({
        ...prevStyle,
        opacity: 0,
      }));
    }
  }, [updateHighlight]);

  useEffect(() => {
    window.addEventListener("resize", handleResize);
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, [handleResize]);

  const storeNavLinkRef = useCallback((link) => {
    if (link !== null && !navLinksRef.current.includes(link)) {
      navLinksRef.current.push(link);
    }
  }, []);

  return (
    <header className="myHeader">
      <nav>
        <input type="checkbox" className="check" />
        <label htmlFor="check" className="toggleResponsiveMenu">
          <i className="fas fa-bars"></i>
        </label>

        <div className="logoJagua">
          <Link to="/" className="navLink">
            <img src={jaguaLogoGray} alt="Logo do site" />
          </Link>
        </div>

        <ul>
          <li>
            <Link
              to="/"
              className="navLink"
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
              ref={storeNavLinkRef}
            >
              Home
            </Link>
          </li>
          <li>
            <Link
              to="/aboutMe"
              className="navLink"
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
              ref={storeNavLinkRef}
            >
              Sobre mim
            </Link>
          </li>
          <li>
            <Link
              to="/stickers"
              className="navLink"
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
              ref={storeNavLinkRef}
            >
              Adesivos
            </Link>
          </li>
          <li>
            <Link
              to="/prints"
              className="navLink"
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
              ref={storeNavLinkRef}
            >
              Prints
            </Link>
          </li>
          <li>
            <Link
              to="/shirts"
              className="navLink"
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
              ref={storeNavLinkRef}
            >
              Camisetas
            </Link>
          </li>

          <div id="highlightNavBar" style={highlightStyle}></div>
        </ul>

        <Link to="/login" className="loginButton">
          Entrar
        </Link>
        <div className="smartphoneLogin">
          <Link to="/login">
            <i className="fas fa-sign-in-alt"></i>
          </Link>
        </div>
      </nav>

      <Outlet />
    </header>
  );
};

export default Navbar;
